import React, { Component } from 'react'
import {connect} from 'react-redux';
import PropTypes  from 'prop-types';
import Moment from 'react-moment';
import {deleteEducation} from '../../actions/profileActions';
 class Education extends Component {

    onDeleteClick(id){
        this.props.deleteEducation(id)
    }
    render() {
        const education =this.props.education
            .map(edu =>(
            <tr key={edu._id}>

                <td>{edu.school}</td>
                <td>{edu.degree}</td>
                <td>{edu.fieldsofstudy}</td>
                <td><Moment format="YYYY/MM/DD">{edu.from}</Moment> -
          {edu.to === null ? (
            ' Now'
          ) : (
            <Moment format="YYYY/MM/DD">{edu.to}</Moment>
          )}</td>
                <td><button type="button" onClick={this.onDeleteClick.bind(this,edu._id)} className="btn btn-danger">Delete</button></td>
            </tr>
        ))
        return (
            <div className="">
                <h4 className="mb-4"> Education Credentials</h4>
                <table className="table">
                    <thead>
                        <tr>
                            <th>School</th>
                            <th>Degree</th>
                            <th>Fields of Study</th>
                            <th></th>
                        </tr>
                        <tbody>
                           
                          {education}
                        </tbody>
                    </thead>
                </table>
                
            </div>
        )
    }
}
deleteEducation.propTypes={
    deleteEducation:PropTypes.func.isRequired
}
export default connect(null, {deleteEducation})(Education);
