import React, { Component } from 'react'
import {connect} from 'react-redux';
import PropTypes  from 'prop-types';
import { withRouter } from 'react-router';
import {deleteExperience} from '../../actions/profileActions';
 class Experience extends Component {

    onDeleteClick(id){
        this.props.deleteExperience(id,this.props.history)
    }
    render() {
        const experience =this.props.experience
            .map(exp =>(
            <tr key={exp._id}>

                <td>{exp.company}</td>
                <td>{exp.title}</td>
                <td>{exp.from} -{exp.to}</td>
                <td><button type="button" onClick={this.onDeleteClick.bind(this,exp._id)} className="btn btn-danger">Delete</button></td>
            </tr>
        ))
        return (
            <div className="">
                <h4 className="mb-4"> Experience Credentials</h4>
                <table className="table">
                    <thead>
                        <tr>
                            <th>Company</th>
                            <th>Title</th>
                            <th>Years</th>
                            <th></th>
                        </tr>
                        <tbody>
                           
                          {experience}
                        </tbody>
                    </thead>
                </table>
                
            </div>
        )
    }
}
deleteExperience.propTypes={
    deleteExperience:PropTypes.func.isRequired
}
export default connect(null, {deleteExperience})(withRouter(Experience));
