import React, { Component } from 'react';
import propsTypes from 'prop-types';
import classnames from 'classnames';
import {connect } from 'react-redux';
import {withRouter} from 'react-router-dom'
import {registerUser} from '../../actions/authAction';
import TextFieldGroup from '../common/TextFieldGroup';
 class Register extends Component {


    constructor() {
        super();
        this.state = {
            name:'',
            email:'',
            password:'',
            password2:'',
            errors:'',
        }
        this.onChange =this.onChange.bind(this);
        this.onSubmit =this.onSubmit.bind(this);
    }
    componentDidMount() {
      if(this.props.auth.isAuthenticated) {
        this.props.history.push('dashboard');
      }
    }
    componentWillReceiveProps(nextProps) {
      if(nextProps.errors) {
        this.setState({errors:nextProps.errors.errors})
      }
    }
    onChange(event) {
        this.setState({[event.target.name] : event.target.value})
    }
    onSubmit(e) {
        e.preventDefault();
        const newUser ={
            name:this.state.name,
            email:this.state.email,
            password:this.state.password,
            password2:this.state.password2
        }
        this.props.registerUser(newUser, this.props.history)
        // axios.post('/api/users/register', newUser)
        //       .then(res =>console.log(res.data))
        //         .catch(err =>this.setState({errors:err.response.data}))
    }
    render() {
      const {errors} =this.state;
      
        return (
         
            <div className="register">
            <div className="container">
              <div className="row">
                
                <div className="col-md-8 m-auto">
                  <h1 className="display-4 text-center">Sign Up</h1>
                  <p className="lead text-center">Create your DevConnector account</p>
                  <form onSubmit={this.onSubmit}>
                    <div className="form-group">
                     
                      <input type="text" className={classnames('form-control form-control-lg',{'is-invalid':errors.name})}
                       placeholder="Name" name="name" value={this.state.name} onChange={this.onChange}/>
                       {errors.name &&  (<div className="invalid-feedback"> {errors.name}</div>)}
                       
                    </div>
                    <TextFieldGroup type="text"
                    error={errors.name}
                    
                    />
                    <div className="form-group">
                      <input type="email" className={classnames('form-control form-control-lg',{'is-invalid':errors.email})} 
                      placeholder="Email Address" name="email" 
                      value={this.state.email}
                      onChange={this.onChange} />
                       {errors.email &&  (<div className="invalid-feedback"> {errors.email}</div>)}
                      <small classNameName="form-text text-muted">This site uses Gravatar so if you want a profile image, use a Gravatar email</small>
                    </div>
                    <div className="form-group">
                      <input type="password" className={classnames('form-control form-control-lg',{'is-invalid':errors.password})}
                       placeholder="Password" name="password" value={this.state.password}
                       onChange={this.onChange} />
                        {errors.password &&  (<div className="invalid-feedback"> {errors.password}</div>)}
                    </div>
                    <div className="form-group">
                      <input type="password" className={classnames('form-control form-control-lg',{'is-invalid':errors.password2})}
                       placeholder="Confirm Password" name="password2" value={this.state.password2}
                       onChange={this.onChange}/>
                        {errors.password2 &&  (<div className="invalid-feedback"> {errors.password2}</div>)}
                    </div>
                    <input type="submit" className="btn btn-info btn-block mt-4" />
                  </form>
                </div>
              </div>
            </div>
          </div>
        
        )
    }
}

Register.propsTypes = {
  registerUser:propsTypes.func.isRequired,
  auth:propsTypes.object.isRequired,
  errors:propsTypes.object.isRequired
}

const mapstateToprops =(state) =>({
  auth:state.auth,
  errors:state.errors
})

export default connect(mapstateToprops, {registerUser})(withRouter(Register));
