import axios from "axios";

import { GET_POST,
    GET_POSTS,
    ADD_POST,
    DELETE_POST,
    POST_LOADING, 
    GET_ERRORS} from "./types";

export const addPost = (postData) => (dispatch) => {
    axios
    .post('api/post', postData)
      .then(res =>
        dispatch({
            type:ADD_POST,
            payload:res.data,
        }))
        .catch(err =>
            dispatch({
                type:GET_ERRORS,
                payload:err.response.data
            }))
}

export const getPosts = () => (dispatch) => {
    dispatch(setLoading());
    axios
    .get('api/post')
      .then(res =>
        dispatch({
            type:GET_POSTS,
            payload:res.data,
        }))
        .catch(err =>
            dispatch({
                type:GET_POSTS,
                payload:null
            }))
}
export const deletePost = () => (dispatch) => {
    dispatch(setLoading());
    axios
    .get('api/post')
      .then(res =>
        dispatch({
            type:GET_POSTS,
            payload:res.data,
        }))
        .catch(err =>
            dispatch({
                type:GET_POSTS,
                payload:null
            }))
}
export const setLoading = () => {

    return {
        type:POST_LOADING 
    }
}
