import axios from "axios";

import { GET_POST,
    GET_POSTS,
    ADD_POST,
    DELETE_POST, 
    GET_ERRORS} from "./types";

export const addPost = (postData) => (dispatch) => {
    axios
    .post('api/post', )
      .then(res =>
        dispatch({
            type:ADD_POST,
            payload:res.data,
        }))
        .catch(err =>
            dispatch({
                type:GET_ERRORS,
                payload:err.response.data
            }))
}
