import { 
    ADD_POST,
    DELETE_POST,
    GET_POST,
    GET_POSTS,
    POST_LOADING,
    CLEAR_ERRORS
 } from "../actions/types";

const initialState={
    posts:[],
    post:{},
    loading:false
}

export default function (state=initialState, action) {
    switch(action.type){
        case ADD_POST:
            return {
                ...state,
                posts:[action.payload, ...state.posts]
            }
        case POST_LOADING:
            return {
                ...state,
                loading:true
            }
        case GET_POSTS:
            return {
                ...state,
                posts:action.payload,
                loading:false
            }
        case GET_POST :
            return {
                ...state,
                post:action.payload,
                loading:false
            }
        case DELETE_POST:
                return {
                    ...state,
                    posts:state.posts.filter(post =>post._id !== action.payload)
                }
                case Clear:
                    return {
                        ...state,
                        posts:state.posts.filter(post =>post._id !== action.payload)
                    }
        default:
            return state;
    }
}
