import {Component, useState,useEffect} from 'react'

//using REact hooks

const   App=()=> {

  const [count, setcount] = useState(0)
  useEffect(()=>{
     document.title =`The component did mount ${this.state.count}`
  })
  const increament=()=>{

    setcount(count +1)
  }
  const decreament=()=>{

    setcount(count -1)
  }
  return (
    <div>
     <h1>{count}</h1>
    <button onClick={increament}> increament</button>
    <button onClick={decreament}> decreament</button>
    </div>

  );
} 




