import React, { Component } from 'react'
import isEmpty from '../../validation/is-empty';


class ProfileAbout extends Component {
    render() {
        const {profile} =this.props;
        const firstName=profile.user.name.trim().split(' ')[0]

        const skills= profile.skills.map((skill, index) =>{
            <div key={index} className="p-3">
                 <i className="fa fa-check"/> {s}
            </div>
        })
        return (
            <div className="row">
            <div className="col-md-12">
              <div className="card card-body bg-light mb-3">
                <h3 className="text-center text-info">{firstName} Bio</h3>
                <p className="lead">{isEmpty(profile.bio) ? null :<span>{profile.bio}</span>}
                </p>
                <hr />
                <h3 className="text-center text-info">Skill Set</h3>
                <div className="row">
                  <div className="d-flex flex-wrap justify-content-center align-items-center">
                    <div className="p-3">
                      <i className="fa fa-check"></i> HTML</div>
                    <div className="p-3">
                      <i className="fa fa-check"></i> CSS</div>
                    <div className="p-3">
                      <i className="fa fa-check"></i> JavaScript</div>
                    <div className="p-3">
                      <i className="fa fa-check"></i> Python</div>
                    <div className="p-3">
                      <i className="fa fa-check"></i> C#</div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        )
    }
}
export default ProfileAbout;
